import React, { useState, useEffect } from 'react';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import '../node_modules/flickity/dist/flickity.pkgd.min.js';
import Navbar from './components/Heading/Navbar';
import Footer from './components/Footer/Footer';
import Carousel from './components/Carousel/Carousel';
import MusicPlayer from './components/Music Player/MusicPlayer'
import CardMusic from './components/Card/CardMusic'
import CardGroup from './components/Card/CardGroup'
import TopChart from './components/TopChart/TopChart';
import Favourites from './components/Favourites/Favourites';
import IconButton from '@material-ui/core/IconButton';
import FavoriteRoundedIcon from '@material-ui/icons/FavoriteRounded';
import { Credentials } from './Credentials';
import axios from "axios";

function App() {

    const spotify = Credentials();

    console.log('RENDERING APP.JS');



    const [token, setToken] = useState('');
    const [playlist, setPlaylistId] = useState({ playlistId: ''});
    const [genres, setGenres] = useState({ selectedGenre: '', listOfGenresFromAPI: [] });
    const [tracks, setTracks] = useState({ selectedTrack: '', listOfTracksFromAPI: [] });

    useEffect(() => {

        axios('https://accounts.spotify.com/api/token', {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)
            },
            data: 'grant_type=client_credentials',
            method: 'POST'
        })
            .then(tokenResponse => {
                setToken(tokenResponse.data.access_token);

                axios('https://api.spotify.com/v1/browse/categories?locale=sv_US', {
                    method: 'GET',
                    headers: { 'Authorization': 'Bearer ' + tokenResponse.data.access_token }
                })
                    .then(genreResponse => {
                        setGenres({
                            selectedGenre: genres.selectedGenre,
                            listOfGenresFromAPI: genreResponse.data.categories.items
                        })
                    });

            });

    }, [genres.selectedGenre, spotify.ClientId, spotify.ClientSecret]);


    const buttonClicked = () => {

        axios(`https://api.spotify.com/v1/search?q=Global%20Top%2050&type=playlist`, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            }
        })
            .then(tracksResponse => {
                setTracks({
                    selectedTrack: tracks.selectedTrack,
                    listOfTracksFromAPI: tracksResponse.data.items
                })
                setPlaylistId({
                    playlistId:tracksResponse.data.playlists.items[0].id
                })

                console.log(playlistId);
                console.log(tracksResponse.data.playlists);
            });
    }

    const GetPlaylist = () => {

        console.log(5);
        console.log(playlistId);

        axios(`https://api.spotify.com/v1/playlists/${playlistId}/tracks `, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            }
        })
            .then(tracksResponse => {

                console.log(tracksResponse);
            });
    }

    // const getNewReleases = val => {
    //     axios(`https://api.spotify.com/v1/browse/new-releases`, {
    //         method: 'GET',
    //         headers: { 'Authorization': 'Bearer ' + token }
    //     })
    //         .then(playlistResponse => {
    //         });

    //     console.log(val);
    // }

    return (
        <>
            <Navbar />
            <Carousel />
            <br></br>
            {/* {getNewReleases} */}
            <TopChart />
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <IconButton onClick={buttonClicked}>
                <FavoriteRoundedIcon></FavoriteRoundedIcon>
            </IconButton>
            <IconButton onClick={GetPlaylist}>
                <FavoriteRoundedIcon></FavoriteRoundedIcon>
            </IconButton>

            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <Favourites />
            <Footer />
        </>
    );
}
export default App;
