import React, { useState } from "react";
import IconButton from '@material-ui/core/IconButton';
import SearchRoundedIcon from '@material-ui/icons/SearchRounded';
import axios from "axios";
import { Credentials } from "./../../Credentials";
import CardGroupSearch from "./../Card/CardGroupSearch";

class SearchBar extends React.Component {



    constructor(props) {
        super(props);
        this.state = {
            query: '',
            results: {},
            token: '',
            loading: false,
            message: '',
        };
        this.cancel = '';
    }


    fetchSearchResults = (updatedPageNo = '', query, mytoken) => {
        const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';	// By default the limit of results is 20
        const searchUrl = `https://api.spotify.com/v1/search?q=${query}&type=track`; if (this.cancel) {
            // Cancel the previous request before making a new request
            this.cancel.cancel();
        }
        // Create a new CancelToken
        this.cancel = axios.CancelToken.source();
        axios(searchUrl, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + mytoken,

            },
            cancelToken: this.cancel.token
        })

            .then((res) => {
                console.log(res);
                const resultNotFoundMsg = !res.data.hits.length
                    ? 'There are no more search results. Please try a new search.'
                    : '';
                let tempArray = [];
                for (let i = 0; i < res.data.tracks.items.length; i++) {
                    tempArray = [...tempArray, {
                        index: tempArray.length,
                        artists: res.data.tracks.items[i].track.artists[0].name,
                        spotifyLink: res.data.tracks.items[i].track.external_urls.spotify,
                        trackId: res.data.tracks.items[i].track.id,
                        imageUrl: res.data.tracks.items[i].track.album.images[1].url,
                        trackName: res.data.tracks.items[i].track.name,
                        trackPreviewUrl: res.data.tracks.items[i].track.preview_url
                    }]
                }
                // console.log(tempArray)
                this.setState({

                    results: res,
                    message: resultNotFoundMsg,
                    loading: false,
                })
            })
            .catch((error) => {
                if (axios.isCancel(error) || error) {
                    this.setState({
                        loading: false,
                        message: 'Failed to fetch results.Please check network',
                    });
                }
            });
    };

    getToken = (query) => {
        const spotify = Credentials();

        console.log('RENDERING APP.JS');

        axios('https://accounts.spotify.com/api/token', {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)
            },
            data: 'grant_type=client_credentials',
            method: 'POST'
        })
            .then(tokenResponse => {
                // console.log(tokenResponse)
                let curr = tokenResponse.data.access_token
                this.fetchSearchResults(1, query, curr)
                this.setState({ token: curr })
            });
    }

    handleOnInputChange = (event) => {
        console.log("typed")
        const query = event.target.value;
        if (!query) {
            this.setState({ query, results: {}, message: '' });
        } else {
            this.setState({ query, loading: true, message: '' }, () => {
                this.getToken(query);
            });
        }
    };

    renderSearchResults = () => {
        const results = this.state.results;	
        // console.log(this.state.results)
        if (Object.keys(results).length && results.length) {
            return (
                <CardGroupSearch params={results}></CardGroupSearch>

            );
        }
    };

    render() {
        return (
            <div>
                <form className="d-flex align-items-center">
                    <input
                        className="form-control d-inline"
                        id="search-input"
                        type="search"
                        style={{ borderRadius: "16px" }}
                        placeholder="Search for song"
                        aria-label="Search"
                        onChange={this.handleOnInputChange}
                    />
                    <IconButton type="submit" href="/search">
                        <SearchRoundedIcon color="primary" />
                    </IconButton>

                    <br></br>
                    <br></br>
                    <br></br>
                    <br></br>
                </form>
                {this.renderSearchResults()}
            </div>
        )
    }
}

export default SearchBar;
