import React, { useState, useEffect, Component } from 'react';
import { render } from 'react-dom';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import '../node_modules/flickity/dist/flickity.pkgd.min.js';
import Navbar from './components/Heading/Navbar';
import Footer from './components/Footer/Footer';
import Carousel from './components/Carousel/Carousel';
import MusicPlayer from './components/Music Player/MusicPlayer'
import CardMusic from './components/Card/CardMusic'
import CardGroup from './components/Card/CardGroup'
import TopChart from './components/TopChart/TopChart';
import Favourites from './components/Favourites/Favourites';
import IconButton from '@material-ui/core/IconButton';
import FavoriteRoundedIcon from '@material-ui/icons/FavoriteRounded';
import { Credentials } from './Credentials';
import axios from "axios";

function App() {

    const spotify = Credentials();

    console.log('RENDERING APP.JS');

    const [token, setToken] = useState('');
    const [playlistId, setPlaylistId] = useState('');
    const [genres, setGenres] = useState({ selectedGenre: '', listOfGenresFromAPI: [] });
    const [tracks, setTracks] = useState([]);

    useEffect(() => {

        axios('https://accounts.spotify.com/api/token', {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)
            },
            data: 'grant_type=client_credentials',
            method: 'POST'
        })
            .then(tokenResponse => {
                setToken(tokenResponse.data.access_token);

                axios(`https://api.spotify.com/v1/search?q=Global%20Top%2050&type=playlist`, {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + token
                    }
                })
                    .then(tracksResponse => {

                        setPlaylistId(tracksResponse.data.playlists.items[0].id)

                        console.log(playlistId);
                        console.log(tracksResponse.data.playlists);
                    })
                    .then({ GetPlaylist });

            });

    }, [spotify.ClientId, spotify.ClientSecret]);


    const GetPlaylist = async () => {

        console.log(5);
        console.log(playlistId);

        axios(`https://api.spotify.com/v1/playlists/${playlistId}/tracks `, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            }
        })
            .then(tracksResponse => {
                let tempArray = [];
                for (let i = 0; i < tracksResponse.data.items.length; i++) {
                    tempArray = [...tempArray, {
                        index: tempArray.length,
                        artists: tracksResponse.data.items[i].track.artists[0].name,
                        spotifyLink: tracksResponse.data.items[i].track.external_urls.spotify,
                        trackId: tracksResponse.data.items[i].track.id,
                        imageUrl: tracksResponse.data.items[i].track.album.images[1].url,
                        trackName: tracksResponse.data.items[i].track.name,
                        trackPreviewUrl: tracksResponse.data.items[i].track.preview_url
                    }]
                }
                console.log(tempArray);
                setTracks(tempArray);
            });
    }

    const buttonClicked = async () => {



    }

    return (
        <>
            {/* {buttonClicked}
            {GetPlaylist} */}
            <Navbar />
            <Carousel />
            <br></br>
            {/* {getNewReleases} */}
            <TopChart props={tracks} />
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            {/* <IconButton>
                <FavoriteRoundedIcon></FavoriteRoundedIcon>
            </IconButton>
            <IconButton>
                <FavoriteRoundedIcon></FavoriteRoundedIcon>
            </IconButton> */}
            <IconButton onClick={buttonClicked}>
                <FavoriteRoundedIcon></FavoriteRoundedIcon>
            </IconButton>
            <IconButton onClick={GetPlaylist}>
                <FavoriteRoundedIcon></FavoriteRoundedIcon>
            </IconButton>
            {console.log(tracks)}
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <Favourites />
            <Footer />
        </>
    );
}
export default App;
