import React, { useState } from "react";
import IconButton from '@material-ui/core/IconButton';
import SearchRoundedIcon from '@material-ui/icons/SearchRounded';
import axios from "axios";
import { Credentials } from "./../../Credentials";

class SearchBar extends React.Component {



    constructor(props) {
        super(props);
        this.state = {
            query: '',
            results: {},
            token: '',
            loading: false,
            message: '',
        };
        this.cancel = '';
    }

    handleOnInputChange = (event) => {
        const query = event.target.value;
        if ( ! query ) {
            this.setState({ query, results: {}, message: '' } );
        } else {
            this.setState({ query, loading: true, message: '' }, () => {
                this.fetchSearchResults(1, query);
            });
        }
    };

    fetchSearchResults = (updatedPageNo = '', query) => {
        const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';	// By default the limit of results is 20
        const searchUrl = `https://api.spotify.com/v1/search?q=${query}&type=track`; if (this.cancel) {
            // Cancel the previous request before making a new request
            this.cancel.cancel();
        }
        // Create a new CancelToken
        this.cancel = axios.CancelToken.source(); axios
            .get(searchUrl, {
                cancelToken: this.cancel.token,
            })
            .then((res) => {
                const resultNotFoundMsg = !res.data.hits.length
                    ? 'There are no more search results. Please try a new search.'
                    : ''; this.setState({
                        results: res.data.hits,
                        message: resultNotFoundMsg,
                        loading: false,
                    });
            })
            .catch((error) => {
                if (axios.isCancel(error) || error) {
                    this.setState({
                        loading: false,
                        message: 'Failed to fetch results.Please check network',
                    });
                }
            });
    };

    getToken = () => {
        const spotify = Credentials();

        console.log('RENDERING APP.JS');

        axios('https://accounts.spotify.com/api/token', {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)
            },
            data: 'grant_type=client_credentials',
            method: 'POST'
        })
            .then(tokenResponse => {
                // console.log(tokenResponse)
                let curr = tokenResponse.data.access_token
                this.setState({ token: curr })
            });
    }


    render() {
        return (
            <div>
                <form className="d-flex align-items-center">
                    <input
                        className="form-control d-inline"
                        id="search-input" value=""
                        type="search"
                        style={{ borderRadius: "16px" }}
                        placeholder="Search for song"
                        aria-label="Search"
                        onChange={this.handleOnInputChange}
                    />
                    <IconButton type="submit" href="/search">
                        <SearchRoundedIcon color="primary" />
                    </IconButton>
                </form>
            </div>
        )
    }
}

export default SearchBar;
